close all;clear all;gamma=0.99;a=1-gamma;b=1;m=1;x(1)=0;t(1)=0;y(1)=0;tm(1)=0;ym(1)=0;tmp(1)=0;ymp(1)=0;alpha=0.5;alphamp=0.5;iters=10000;for i=2:itersx(i)=x(i-1)+alpha*(b-a*x(i-1));y(i)=y(i-1)+alpha*(b-a*t(i-1)-m*y(i-1));t(i)=t(i-1)+alpha*a*y(i-1);ym(i)=ymp(i-1)+alphamp*(b-a*tmp(i-1)-ymp(i-1));tm(i)=tmp(i-1)+alphamp*a*ymp(i-1);ymp(i)=ymp(i-1)+alphamp*(b-a*tm(i)-m*ym(i));tmp(i)=tmp(i-1)+alphamp*a*ym(i);x_mean(i-1)=mean(x);y_mean(i-1)=mean(y);t_mean(i-1)=mean(t);tmp_mean(i-1)=mean(tmp);ymp_mean(i-1)=mean(ymp);err_gtd(i-1)=norm(t_mean(i-1)-inv(a)*b);err_gtdmp(i-1)=norm(tmp_mean(i-1)-inv(a)*b);err_td(i-1)=norm(x_mean(i-1)-inv(a)*b);end;hold on;plot(err_gtdmp,'k');plot(err_gtd,'r');plot(err_td);fptr=fopen('err_td_one','w');for i=1:1000:itersfprintf(fptr,'%d\t%e\n',i,err_td(i));end;fclose(fptr);fptr=fopen('err_gtd_one','w');for i=1:1000:itersfprintf(fptr,'%d\t%e\n',i,err_gtd(i));end;fclose(fptr);fptr=fopen('err_gtdmp_one','w');for i=1:1000:itersfprintf(fptr,'%d\t%e\n',i,err_gtdmp(i));end;fclose(fptr);