function [x,y]= osci(iters,alpha)x=zeros(iters,1);y=zeros(iters,1);x(1)=1;y(1)=1;for i=1:iters  if(rand<0.5)%  x(i+1)=x(i)+alpha*(0 -1*x(i)); x(i+1)=x(i)+alpha*(0 +1*x(i));  else%  x(i+1)=x(i)+alpha*(0-4*x(i));  x(i+1)=x(i)+alpha*(0 -2*x(i));  end;y(i+1)=y(i)+alpha*y(i)*(-0.5);end;x_final=x(iters);y_final=y(iters);%iters=20;%alpha=0.5;%x=zeros(iters,1);%y=zeros(iters,1);%x(1)=1;%y(1)=1;%for i=1:iters%if(rand<0.5)%x(i+1)=x(i)+alpha*x(i)*(-1);%else%x(i+1)=x(i)+alpha*x(i)*(-0.5);%end;%y(i+1)=y(i)+alpha*y(i)*(-0.75); %end;